# 8,5/10 et 59,3% de ressemblance 

def pourcentage_ressemblance(text):
    p = 0
    M = len(text)
    for s in text:
        l = list_cause_consequence(s["text"])
        l1 = l[0]
        l2 = s["condition"].split()

        l3 = l[1]
        l4 = s["consequence"].split()
        p += compare_lists(l1, l2)+compare_lists(l3, l4)
    return p/(2*M)*100


